import {log} from './App'
import { Button, Heading } from '@chakra-ui/react'
import{useEffect, useState} from 'react'
export default function About(){
   const [page,setpa]=useState("Home")
   useEffect(() => {
      document.title = page;  
    }, [page]);
     return(
        <>
<log.Consumer>
   {(x)=>{
      return(
         <>
{setpa(x.user)}

         <Heading>{x.user}</Heading>
         <Button onClick={x.lhandler}>Log out</Button>
         </>
      )
   }}
        </log.Consumer>
        </>
     )
}
